import React from 'react'
import { useState } from 'react';

function Sayac() {

    const [number,setNumber]=useState(0);

    const decrease=()=>{
        setNumber(number-1)
    };
    const increase =()=> {
        setNumber(number+1)
    };

    return (
        <div>
            <h1>{number}</h1>

            <button onClick={decrease}>Decrease</button>
            <button onClick={decre}>Increase</button>
        </div>
    )
}

export default Sayac;
