import { useState } from "react";


function App() {

const [name, setName]= useState("Mehmet");
const [age,setAge]=useState(29);
const [friends,setFriends]=useState(["Ahmet","Murat"]);
const [address, setAddress]=useState({title:"Istanbul-Atasehir", zip:346747});



  return (
    <div className="App">
   <h1>Merhaba {name}</h1>
   <h1>{age}</h1>
   <button onClick={()=>setName("Ahmet")}>Change Name</button>
   <button onClick={()=>setAge(30)}>Change Age</button>


<hr/>
<h1>FRIENDS</h1>



{friends.map((friend,index)=>(
  <div key={index}>{friend}</div>
))}

<br/>

<button onClick={()=>setFriends([...friends,"Ayse"])}>ADD NEW FRIEND</button>

<hr/>
<h1>ADDRESS</h1>

<div>{address.title}  {address.zip}</div>

<button onClick={()=>setAddress({})}>Change Address</button>

    </div>
  );
}

export default App;
